  render() {
    return (
      <Wrapper>
        <div class="container">
        <div class="jumbotron">

        <Title> Game of Thrones Clicky Game
          <br></br>
        <p>Current Score: {this.state.curScore} | Top Score: {this.state.topScore}</p>
        </Title> 
        </div>
        </div>

        {this.state.friends.map(friend => (
          <FriendCard
            chooseFriend={this.chooseFriend}
            handleIncrement={this.handleIncrement}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}

          />

        ))}
      </Wrapper>
    );
  }
}

export default App;
